                               }                                });                                var infowindow = new google.maps.InfoWindow({                                    maxWidth: 160                                });                                var markers = new Array();                                var iconCounter = 0;                                // Add the markers and infowindows to the map                                for (var i = 0; i < locations.length; i++) {                                    var marker = new google.maps.Marker({                                        position: new google.maps.LatLng(locations[i][1], locations[i][2]),                                        map: map,                                        icon: icons[iconCounter]                                    });                                    markers.push(marker);                                    google.maps.event.addListener(marker, 'click', (function (marker, i) {                                        return function () {                                            infowindow.setContent(locations[i][0]);                                            infowindow.open(map, marker);                                        }                                    })(marker, i));                                    iconCounter++;                                    // We only have a limited number of possible icon colors, so we may have to restart the counter                                    if (iconCounter >= iconsLength) {                                        iconCounter = 0;                                    }                                }                                function autoCenter() {                                    //  Create a new viewpoint bound                                    var bounds = new google.maps.LatLngBounds();                                    //  Go through each...                                    for (var i = 0; i < markers.length; i++) {                                        bounds.extend(markers[i].position);                                    }                                    //  Fit these bounds to the map                                    map.fitBounds(bounds);                                }                                autoCenter();                            </script>                         </div>                    </div>                </div>            </div>            <!--            <div class="col-lg-12 ">                            <h2>Order Map</h2>              </div>-->            <section id='footer'>                <?php // $this->load->view(THEME . 'layout/inc-footer'); ?>            </section>        </section>    </section>    <?php    $this->load->view(THEME . 'layout/inc-before-body-close');    echo $CI->assets->renderFooter();    ?></body></html>