<?php$this->load->model('catalogue/Productmodel');if (count($this->cart->contents()) > 0) {    foreach ($this->cart->contents() as $key => $cartProduct) {        ?>        <li>            <div class="media">                 <div class="media-heading">                    <div class="item-info">                        <span><?php echo $cartProduct['name']; ?></span>                    </div>                </div>                  <div class="media-body">                     <div style="width: 50%;float: left;">                         <div style="padding-left: 5px;" class="pad-top20">                            <a href="#">                                 <img  alt="" src="<?php echo com_get_image('PRODUCT_IMAGE_URL', 'PRODUCT_IMAGE_PATH', 'PRODUCT_RESIZE_IMAGE_URL', 'PRODUCT_RESIZE_IMAGE_PATH', $this->Productmodel->getProductImageBySku($cartProduct['product_sku']), 350, 500) ?>" alt="<?php echo $cartProduct['name'] ?>" class="img-responsive" >                            </a>                         </div>                     </div>                    <div style="width: 50%;float: left;">                         <div class="item-info pad-top30">                            <div>Qty : <?php echo $cartProduct['qty']; ?></div>                            <div>Price : <?= MCC_CURRENCY_SYMBOL . $cartProduct['price']; ?></div>                            <span  class="mar-top10 btn btn-xs btn-danger pull-right remove-item" data-product-id="<?php echo $key ?>">x</span>                        </div>                    </div>                </div>             </div>        </li>        <li><hr style="margin: 0;padding: 0;"></li>    <?php }    ?>    <div class="media">         <div class="col-md-12" style="text-align:center; background-color: rgb(255, 255, 255);" >            <a href="<?php echo createUrl('catalogue/cart'); ?>" >                Checkout            </a>        </div>    </div>    <?php} else {    ?>    <li>No items added in cart.</li><?php } ?>