                                                                <span class="price-sale"><abbr style="text-decoration: line-through;"><?php echo MCC_CURRENCY_SYMBOL ?> <?php echo $detail['product_price'] ?></abbr></span>                                                                <span itemprop="price" class="price price-sale"><?php echo MCC_CURRENCY_SYMBOL ?> <?php echo $specialPrice['price'] ?></span>                                                                <br>                                                                                                                                   <div class="inc-tax">                                                                  (Price Inclusive TAX)                                                                  </div>                                                                                                                                <div class="clearfix"></div>                                                                <div class="main-example">                                                                    <span id="main-example"></span>                                                                    <p class="note-offer-till-only-fixtime"> <i class="fa fa-clock-o" style="color:#333;"></i> offer is for limited time </p>                                                                </div>                                                                <div class="clearfix"></div>                                                                <script id="main-example-template" type="text/template">                                                                    <div class="time <%= label %>">                                                                    <span class="count curr top"><%= curr %></span>                                                                    <span class="count next top"><%= next %></span>                                                                    <span class="count next bottom"><%= next %></span>                                                                    <span class="count curr bottom"><%= curr %></span>                                                                    <span class="label"><%= label.length < 6 ? label : label.substr(0, 3)  %></span>                                                                    </div>                                                                </script>                                                                <script type="text/javascript">                                                                    jQuery(window).on('load', function ($) {                                                                        var labels = ['weeks', 'days', 'hours', 'minutes', 'seconds'],                                                                                nextYear = (new Date().getFullYear() + 1) + '/01/01',                                                                                template = _.template(jQuery('#main-example-template').html()),                                                                                currDate = '00:00:00:00:00',                                                                                nextDate = '00:00:00:00:00',                                                                                parser = /([0-9]{2})/gi,                                                                                $example = jQuery('#main-example');                                                                        // Parse countdown string to an object                                                                        function strfobj(str) {                                                                            var parsed = str.match(parser),                                                                                    obj = {};                                                                            labels.forEach(function (label, i) {                                                                                obj[label] = parsed[i]                                                                            });                                                                            return obj;                                                                        }                                                                        // Return the time components that diffs                                                                        function diff(obj1, obj2) {                                                                            var diff = [];                                                                            labels.forEach(function (key) {                                                                                if (obj1[key] !== obj2[key]) {                                                                                    diff.push(key);                                                                                }                                                                            });                                                                            return diff;                                                                        }                                                                        // Build the layout                                                                        var initData = strfobj(currDate);                                                                        labels.forEach(function (label, i) {                                                                            jQuery('#main-example').append(template({                                                                                curr: initData[label],                                                                                next: initData[label],                                                                                label: label                                                                            }));                                                                        });                                                                        // Starts the countdown                                                                        jQuery('#main-example').countdown(<?php echo $specialPrice['end_time'] ?>, function (event) {                                                                            var newDate = event.strftime('%w:%d:%H:%M:%S'),                                                                                    data;                                                                            if (newDate !== nextDate) {                                                                                currDate = nextDate;                                                                                nextDate = newDate;                                                                                // Setup the data                                                                                data = {                                                                                    'curr': strfobj(currDate),                                                                                    'next': strfobj(nextDate)                                                                                };                                                                                // Apply the new values to each node that changed                                                                                diff(data.curr, data.next).forEach(function (label) {                                                                                    var selector = '.%s'.replace(/%s/, label),                                                                                            $node = jQuery('#main-example').find(selector);                                                                                    // Update the node                                                                                    $node.removeClass('flip');                                                                                    $node.find('.curr').text(data.curr[label]);                                                                                    $node.find('.next').text(data.next[label]);                                                                                    // Wait for a repaint to then flip                                                                                    _.delay(function ($node) {                                                                                        $node.addClass('flip');                                                                                    }, 50, $node);                                                                                });                                                                            }                                                                        });                                                                    });                                                                </script>                                                            </span>                                                        </span>                                                    <?php } else { ?>                                                        <span class="special-price">                                                            <span class="special-price" id="product-price-892">                                                                <span itemprop="price" class="price price-sale"><?php echo MCC_CURRENCY_SYMBOL ?> <?php echo $detail['product_price'] ?></span>                                                                <br>                                                                                                                                 <span class="inc-tax">                                                                  (Price Inclusive TAX)                                                                  </span>                                                                                                                            </span>                                                        </span>                                                    <?php } ?>                                                </div>                                            </div>                                            <div class="stock-availability">                                                <p class="availability in-stock" style="float:left;margin: 20px 0;">Availability:                                                     <?php if ($detail['stock_level'] > 0) { ?>                                                            <span>  In stock </span>                                                    <?php } else { ?>                                                        <abbr style="color:red">Out Of Stock</abbr>                                                    <?php } ?>                                                </p>                                            </div>                                            <div class="short-description">                                                <?php echo $detail['product_description'] ?>                                            </div>                                            <?php                                            $ttlDdl = 0;                                            if ($parent) {                                                ?>                                                <div class="clearfix">                                                    <p  style="float:left;margin: 20px 