x" height="32px;">        </span>        <span id="bpopupContent" style="font-size: 13px;font-weight: 600;padding-left: 5px;position: relative;top:3px;"></span>    </div></div><script type="text/javascript">    jQuery(document).ready(function ($) {        $("#add-to-cart").submit(function (event) {            var form = $(this);            var selectedQt = $('#quantity').val();            if (selectedQt) {                ;                $.post(form.attr('action'),                        {<?= $this->security->get_csrf_token_name(); ?>: '<?= $this->security->get_csrf_hash(); ?>',                            'form-data': $(this).serialize(),                        }                ).done(function (data) {                    data = jQuery.parseJSON(data);                    $("#bpopupContent").html(data.message);                    $("#bpopup").fancybox().trigger('click');                    setTimeout(function () {                        $.fancybox.close();                    }, 2000);                    $('.dropdown-cart').html(data.html)                    $('.item-count').html(data.itemcount);                });                event.preventDefault();                return false;            } else {                alert("Sorry quantity cannot be 0");                return false;            }        });        $('.hideConfAttr > option').hide();        $('.nHideConfAttr , .hideConfAttr').on('change',function(){			            var currAttr = $(this).attr('id').replace('attr-','');            var nextAttr = $(this).data('nattr');            var currOrder = parseInt($(this).data('aorder'));            var thsValue = $(this).val();             var selOptTxt = $(this).find('option:selected').text();            var lblAttr = $(this).data('lblatr');            var ttlAttr =  <?= $ttlDdl; ?>;            if( lblAttr == selOptTxt ) {                makeHide(currOrder,  ttlAttr);            } else if(nextAttr) {                /*  Make all below hide*/                makeHide(currOrder,  ttlAttr);                $.get('<?= base_url( 'catalogue/confProdAttr/' ); ?>',                     {                           'form_data':$( "#add-to-cart" ).serialize(),                        'next_attr':nextAttr,                    },                     function(data){                        data = JSON.parse(data);                        if(data.success){                            $('#attr-'+nextAttr).html(data.html);                                                    }                  });                 $('#attr-'+nextAttr +' option[value="0"]').show();                     }        });        function makeHide( orderStart, ttlCount ){        for(var i= (orderStart+1) ; i < ttlCount; i++)        {             $('select[data-aorder="'+i+'"] option[value!=""]').remove();            }        }    });</script>